/// <reference types="node" />

import { DumpOptions } from 'js-yaml';
import { LoadOptions } from 'js-yaml';

/**
 * load .yml or .yaml
 *
 * @example
 * ```ts
 * import { loadYml } from 'load-yml'
 * const data = await loadYml('/path/of/yaml') // \{ "name": "saqqdy" \}
 * ```
 * @param path - file path
 * @returns - result
 */
export declare function loadYml(path: string | Buffer | URL): Promise<unknown>;

/**
 * load .yml or .yaml sync
 *
 * @example
 * ```ts
 * import { loadYmlSync } from 'load-yml'
 * const data = loadYmlSync('/path/of/yaml') // \{ "name": "saqqdy" \}
 * ```
 * @param path - file path
 * @returns - result
 */
export declare function loadYmlSync(path: string | Buffer | URL): unknown;

/**
 * parse yaml data
 *
 * @param data - file path
 * @param options - load options: LoadOptions
 * @returns - result
 */
export declare function parseYml(data: string, options?: LoadOptions): unknown;

/**
 * stringify yaml data
 *
 * @param data - file path
 * @param options - dump options: DumpOptions
 * @returns - result
 */
export declare function stringifyYml<T>(data: T, options?: DumpOptions): string;

export declare const version: string;

export { }
