<template>
  <a-form :form="form">
    <a-form-item label="密码" :label-col="{ span: 5 }" :wrapper-col="{ span: 12 }">
      <a-input v-decorator="['password', { rules: [{ required: true, message: 'Please input your note!' }] }]" />
    </a-form-item>
  </a-form>
</template>
<script>
export default {
  props: {
    value: [Array, Object, Number, String]
  },
  data() {
    return {
      form: this.$form.createForm(this)
    };
  },
  async mounted() {
    console.log(this.value)
    this.id = this.value._id;
  },
  methods: {
    affirm(e) {
      let { $cloud, id, editData } = this;
      let { handleCancel } = e;
      this.form.validateFields((err, values) => {
        if (!err) {
          editData(values);
          handleCancel();
        }
      })
    },
    editData(values) {
      this.$cloud.users().edit(this.id,values).then(res => {
        if (res) {
          this.$message.success("更新成功");
        }
      })
    }
  },
};
</script>