<template>
  <a-form :form="form">
    <a-form-item label="角色标题" :label-col="{ span: 5 }" :wrapper-col="{ span: 12 }">
      <a-input v-decorator="['title', { rules: [{ required: true, message: 'Please input your note!' }] }]" />
    </a-form-item>
    <a-form-item label="角色名称" :label-col="{ span: 5 }" :wrapper-col="{ span: 12 }">
      <a-input v-decorator="['name', { rules: [{ required: true, message: 'Please input your note!' }] }]" />
    </a-form-item>
    <a-form-item label="状态" :label-col="{ span: 5 }" :wrapper-col="{ span: 12 }">
      <a-switch v-decorator="['status', { valuePropName: 'checked',initialValue:true }]" checkedChildren="启动" unCheckedChildren="停用" />
    </a-form-item>
  </a-form>
</template>
<script>
export default {
  data() {
    return {
      form: this.$form.createForm(this),
    };
  },
  methods: {
    affirm(e) {
      let { handleCancel } = e;
      let { $cloud,$message } = this;
      this.form.validateFields((err, values) => {
        if (!err) {
          $cloud.roles().add(values).then(res => {
            $message.success("提交成功");
            handleCancel();
          });
        }
      });
    }
  },
};
</script>