// eslint-disable-next-line
import { UserLayout, BasicLayout, PageView, PublicLayout } from '#/layouts'

let { PublicPages } = require('@/package.json');

let Public_Pages = {
    path: '/',
    component: BasicLayout,
    redirect: '',
    hidden: true,
    children: []
}

if (PublicPages) {
    for (let key in PublicPages) {
        //获取第一个页面
        if (!Public_Pages.redirect) {
            Public_Pages.redirect = key;
        }

        //设置参数
        let item = PublicPages[key];

        //设置公共页面
        Public_Pages.children.push({
            meta: {
                title: item.title,
                icon: item.icon || 'none'
            },
            path: key,
            name: key,
            component: () => import('@/views/public/' + key + '.vue')
        })
    }
}


export default Public_Pages;