<template>
  <div class="account-settings-info-view">
    <a-row :gutter="16">
      <a-col :md="24" :lg="16">
        <a-form layout="vertical">
          <a-form-item label="昵称">
            <a-input v-model="userInfo.nickname" />
          </a-form-item>
          <a-form-item label="简介">
            <a-textarea rows="4" v-model="userInfo.describe" />
          </a-form-item>
          <a-form-item label="手机">
            <a-input disabled :value="userInfo.mobile" />
          </a-form-item>
          <a-form-item label="邮箱" disabled>
            <a-input disabled :value="userInfo.email" />
          </a-form-item>
          <a-form-item>
            <a-button type="primary" @click="onSave()">保存</a-button>
          </a-form-item>
        </a-form>
      </a-col>
      <a-col :md="24" :lg="8" :style="{ minHeight: '180px' }">
        <div class="ant-upload-preview" @click="$refs.modal.edit(1)">
          <a-icon type="cloud-upload-o" class="upload-icon" />
          <div class="mask" :style="{ minHeight: '180px' }">
            <a-icon type="plus" />
          </div>
          <img :src="option.img" v-if="option.img"/>
          <img src="../../../assets/headpic.jpeg" v-else>
        </div>
      </a-col>
    </a-row>
    <avatar-modal ref="modal" @ok="setavatar" />
  </div>
</template>
<script>
import AvatarModal from './AvatarModal'

export default {
  components: {
    AvatarModal
  },
  data() {
    return {
      // cropper
      userInfo: {},
      option: {
        img: '/avatar2.jpg',
        info: true,
        size: 1,
        outputType: 'jpeg',
        canScale: false,
        autoCrop: true,
        // 只有自动截图开启 宽度高度才生效
        autoCropWidth: 180,
        autoCropHeight: 180,
        fixedBox: true,
        // 开启宽度和高度比例
        fixed: true,
        fixedNumber: [1, 1]
      }
    }
  },
  /**
   * 页面加载执行
   * @return {[type]} [description]
   */
  mounted() {
    this.getUserInfo()
  },
  methods: {
    setavatar(url) {
      this.option.img = url
    },
    getUserInfo() {
      let that = this;
      this.$cloud.users().get().then(res => {
        that.userInfo = res;
        that.option.img = res.avatar;
      });

    },
    onSave() {
      let data = {
        nickname: this.userInfo.nickname,
        describe: this.userInfo.describe,
        avatar:this.option.img
      }
      this.$cloud.users().edit(this.userInfo.userid,data).then(res => {
        if (res) {
          this.$message.success("保存成功");
          this.getUserInfo()
        }
      });
    }
  }
}
</script>
<style lang="less" scoped>
.avatar-upload-wrapper {
  height: 200px;
  width: 100%;
}

.ant-upload-preview {
  position: relative;
  margin: 0 auto;
  width: 100%;
  max-width: 180px;
  border-radius: 50%;
  box-shadow: 0 0 4px #ccc;

  .upload-icon {
    position: absolute;
    top: 0;
    right: 10px;
    font-size: 1.4rem;
    padding: 0.5rem;
    background: rgba(222, 221, 221, 0.7);
    border-radius: 50%;
    border: 1px solid rgba(0, 0, 0, 0.2);
  }

  .mask {
    opacity: 0;
    position: absolute;
    background: rgba(0, 0, 0, 0.4);
    cursor: pointer;
    transition: opacity 0.4s;

    &:hover {
      opacity: 1;
    }

    i {
      font-size: 2rem;
      position: absolute;
      top: 50%;
      left: 50%;
      margin-left: -1rem;
      margin-top: -1rem;
      color: #d6d6d6;
    }
  }

  img,
  .mask {
    width: 100%;
    max-width: 180px;
    height: 100%;
    border-radius: 50%;
    overflow: hidden;
  }
}
</style>